import { useState, useRef, useCallback } from 'react';
import Calendar from "../components/Calendar";
import PortalPopup from "../components/PortalPopup";
import styles from './Component1.module.css';

const Component1 = ({ onSave, onClose }) => {
  const inputContainerRef = useRef(null);
  const [isCalendarOpen, setCalendarOpen] = useState(false);
  
  // 폼 입력을 위한 상태
  const [projectName, setProjectName] = useState("");
  const [taskName, setTaskName] = useState("");
  const [projectPeriodStart, setProjectPeriodStart] = useState("");
  const [projectPeriodEnd, setProjectPeriodEnd] = useState("");
  const [projectScale, setProjectScale] = useState("");
  const [client, setClient] = useState("");
  const [role, setRole] = useState("");
  const [performanceGrade, setPerformanceGrade] = useState("");
  const [remarks, setRemarks] = useState("");
  const [keywords, setKeywords] = useState({
    정보통신: false,
    인공지능: false,
    빅데이터: false,
    IoT: false,
    블록체인: false,
    바이오: false,
    화학공학: false,
    기계공학: false,
    전자공학: false,
    재료공학: false,
    환경공학: false,
    에너지: false,
    의료기기: false,
    자동차: false,
    항공우주: false,
    건축: false,
    설계: false,
    마케팅: false,
    디자인: false,
    기타: false,
  });

  const openCalendar = useCallback(() => {
    setCalendarOpen(true);
  }, []);

  const closeCalendar = useCallback(() => {
    setCalendarOpen(false);
  }, []);

  const handleKeywordChange = (keyword) => {
    setKeywords((prev) => ({ ...prev, [keyword]: !prev[keyword] }));
  };

  const handleSave = () => {
    const selectedKeywords = Object.keys(keywords).filter((key) => keywords[key]).join(", ");
    const projectData = {
      사업명: projectName,
      과제명: taskName,
      사업기간: `${projectPeriodStart} - ${projectPeriodEnd}`,
      사업규모: projectScale,
      발주처: client,
      수행역할: role,
      성과등급: performanceGrade,
      비고: remarks,
      핵심키워드: selectedKeywords,
    };

    if (onSave) onSave(projectData);
    if (onClose) onClose();
  };

  return (
    <>
      <div className={styles.div}>
        <div className={styles.div1}>
          <b className={styles.b}>핵심키워드</b>
          <div className={styles.rectangleParent}>
            <div className={styles.groupChild} />
            <div className={styles.groupParent}>
              {Object.keys(keywords).map((keyword) => (
                <div className={styles.rectangleGroup} key={keyword}>
                  <div className={styles.groupInner} />
                  <div className={styles.smallCheckParent}>
                    <div className={styles.smallCheck}>
                      <div className={styles.stateLayer}>
                        <div className={styles.container} />
                        {keywords[keyword] && (
                          <img className={styles.checkSmallIcon} alt="" src="check_small.svg" />
                        )}
                      </div>
                    </div>
                    <div className={styles.iot} onClick={() => handleKeywordChange(keyword)}>
                      {keyword}
                    </div>
                  </div>
                </div>
              ))}
            </div>
          </div>
          <div className={styles.component13}>
            <img className={styles.caretrightIcon} alt="" src="CaretRight.svg" />
          </div>
          <div className={styles.component26} />
          <div className={styles.frameParent}>
            <div className={styles.groupContainer}>
              <div className={styles.groupParent1}>
                <div className={styles.parent}>
                  <div className={styles.div21}>사업명</div>
                  <div className={styles.inputBasic}>
                    <div className={styles.bg} />
                    <input
                      className={styles.div22}
                      value={projectName}
                      onChange={(e) => setProjectName(e.target.value)}
                      placeholder="예 : 2025 연구개발 특구 육성 지원 사업"
                    />
                  </div>
                </div>
                <div className={styles.div21}>과제명</div>
                <div className={styles.inputBasic}>
                  <div className={styles.bg} />
                  <input
                    className={styles.div22}
                    value={taskName}
                    onChange={(e) => setTaskName(e.target.value)}
                    placeholder="예 : IoT 기반 스마트팜 플랫폼 개발"
                  />
                </div>
              </div>
              <div className={styles.inputBasicParent}>
                <div className={styles.div26}>발주처</div>
                <div className={styles.inputBasic2}>
                  <div className={styles.inputBasic3}>
                    <input
                      className={styles.div22}
                      value={client}
                      onChange={(e) => setClient(e.target.value)}
                      placeholder="예 : 중소벤처기업부"
                    />
                  </div>
                </div>
                <div className={styles.div27}>사업규모</div>
                <div className={styles.inputBasic}>
                  <div className={styles.bg} />
                  <input
                    className={styles.div22}
                    value={projectScale}
                    onChange={(e) => setProjectScale(e.target.value)}
                    placeholder="예 : 1억 미만"
                  />
                </div>
              </div>
              <div className={styles.group}>
                <div className={styles.div28}>사업기간</div>
                <div className={styles.div29}>수행역할</div>
                <div className={styles.input} ref={inputContainerRef} onClick={openCalendar}>
                  <div className={styles.input1}>
                    <div className={styles.baseInput}>
                      <img className={styles.calendarcheckIcon} alt="" src="CalendarCheck.svg" />
                      <input
                        className={styles.div30}
                        value={projectPeriodStart}
                        onChange={(e) => setProjectPeriodStart(e.target.value)}
                        placeholder="YYYY.MM"
                      />
                    </div>
                  </div>
                  <div className={styles.inputInner}>
                    <div className={styles.frameChild} />
                  </div>
                  <div className={styles.input2}>
                    <div className={styles.baseInput1}>
                      <img className={styles.calendarcheckIcon} alt="" src="CalendarCheck.svg" />
                      <input
                        className={styles.div30}
                        value={projectPeriodEnd}
                        onChange={(e) => setProjectPeriodEnd(e.target.value)}
                        placeholder="YYYY.MM"
                      />
                    </div>
                  </div>
                </div>
              </div>
              <div className={styles.inputBasicGroup}>
                <div className={styles.div33}>성과등급</div>
                <div className={styles.inputBasic4}>
                  <div className={styles.bg} />
                  <select
                    className={styles.div22}
                    value={performanceGrade}
                    onChange={(e) => setPerformanceGrade(e.target.value)}
                  >
                    <option value="">선택</option>
                    <option value="S (우수)">S (우수)</option>
                    <option value="A">A</option>
                    <option value="B">B</option>
                    <option value="-">-</option>
                  </select>
                  <img className={styles.icon} alt="" src="Icon.svg" />
                </div>
                <div className={styles.div35}>수행여부</div>
                <div className={styles.inputBasic5}>
                  <div className={styles.bg} />
                  <select
                    className={styles.div22}
                    value={remarks}
                    onChange={(e) => setRemarks(e.target.value)}
                  >
                    <option value="">선택</option>
                    <option value="완료">완료</option>
                    <option value="수행 중">수행 중</option>
                  </select>
                  <img className={styles.icon} alt="" src="Icon.svg" />
                </div>
              </div>
            </div>
          </div>
          <div className={styles.vectorParent}>
            <img className={styles.frameItem} alt="" src="Rectangle 1136.svg" />
            <b className={styles.b1}>사업 정보 입력</b>
            <img
              className={styles.closeIcon}
              alt=""
              src="close.svg"
              onClick={onClose}
            />
            <button className={styles.component13} onClick={handleSave}>
              <div className={styles.component13Child} />
              <b className={styles.b12}>저장</b>
              <img className={styles.caretrightIcon} alt="" src="CaretRight.svg" />
            </button>
          </div>
        </div>
      </div>
      {isCalendarOpen && (
        <PortalPopup
          overlayColor="rgba(113, 113, 113, 0.3)"
          placement="Bottom left"
          bottom={-40}
          // relativeLayerRef={inputContainerRef} 제거하여 테스트
          onOutsideClick={closeCalendar}
        >
          <Calendar onClose={closeCalendar} />
        </PortalPopup>
      )}
    </>
  );
};

export default Component1;
