import { useCallback, useState } from 'react';
import styles from './css/Companies.module.css';

// 선호분야 드롭다운 컴포넌트
const PreferredFieldsDropdown = ({ value, onChange, placeholder = "선택" }) => {
  const [isOpen, setIsOpen] = useState(false);
  const [selectedFields, setSelectedFields] = useState(value ? value.split(', ').filter(f => f.trim()) : []);

  const fieldOptions = [
    '기계 · 소재',
    '전기 · 전자', 
    '정보 · 통신',
    '화공 · 섬유',
    '바이오 · 의료 · 생명',
    '에너지 · 자원',
    '공예 · 디자인',
    '기타'
  ];

  const handleFieldSelect = (field) => {
    let newFields;
    if (selectedFields.includes(field)) {
      newFields = selectedFields.filter(f => f !== field);
    } else {
      newFields = [...selectedFields, field];
    }
    
    setSelectedFields(newFields);
    onChange(newFields.join(', '));
  };

  const handleRemoveField = (field, e) => {
    e.stopPropagation();
    const newFields = selectedFields.filter(f => f !== field);
    setSelectedFields(newFields);
    onChange(newFields.join(', '));
  };

  return (
    <div className="relative w-full">
      {/* 메인 선택 박스 */}
      <div 
        className="relative w-full h-10 cursor-pointer"
        onClick={() => setIsOpen(!isOpen)}
      >
        <div style={{
          position: 'absolute',
          height: '110%',
          width: '101.14%',
          top: '-5%',
          right: '-0.57%',
          bottom: '-5%',
          left: '-0.57%',
          borderRadius: '5.5px',
          backgroundColor: 'var(--color-dodgerblue-200)'
        }} />
        <div style={{
          position: 'absolute',
          height: '100%',
          width: '100%',
          top: 0,
          right: 0,
          bottom: 0,
          left: 0,
          borderRadius: '5px',
          backgroundColor: 'var(--color-ghostwhite)',
          border: '1.5px solid var(--color-dodgerblue-100)',
          boxSizing: 'border-box'
        }} />
        <div style={{
          position: 'absolute',
          width: 'calc(100% - 52.5px)',
          top: 'calc(50% - 12.5px)',
          left: '15.6px',
          lineHeight: '25px',
          fontWeight: '500',
          display: 'flex',
          alignItems: 'center',
          color: selectedFields.length > 0 ? 'var(--color-gray-100)' : 'var(--color-darkslategray)'
        }}>
          {selectedFields.length > 0 ? `${selectedFields.length}개 선택됨` : placeholder}
        </div>
        <img 
          style={{
            position: 'absolute',
            top: 'calc(50% - 1.93px)',
            right: '15.55px',
            width: '8.7px',
            height: '6px',
            transform: isOpen ? 'rotate(180deg)' : 'rotate(0deg)',
            transition: 'transform 0.2s'
          }}
          alt="" 
          src="Icon.svg" 
        />
      </div>

      {/* 드롭다운 메뉴 */}
      {isOpen && (
        <div style={{
          position: 'absolute',
          top: '40px',
          left: '2px',
          filter: 'drop-shadow(2px 4px 4px rgba(0, 0, 0, 0.15))',
          width: '350px',
          zIndex: 1000,
          backgroundColor: 'white',
          border: '1px solid var(--color-lightsteelblue)',
          borderRadius: '0 0 5px 5px'
        }}>
          {/* 선택된 필드들 표시 */}
          {selectedFields.length > 0 && (
            <div style={{
              padding: '12px',
              borderBottom: '1px solid var(--color-lightsteelblue)',
              display: 'flex',
              flexWrap: 'wrap',
              gap: '8px'
            }}>
              {selectedFields.map((field, index) => (
                <div 
                  key={index}
                  style={{
                    display: 'inline-flex',
                    alignItems: 'center',
                    padding: '4px 12px',
                    backgroundColor: 'var(--color-lavender)',
                    color: 'var(--color-darkslateblue)',
                    borderRadius: '20px',
                    fontSize: '14px'
                  }}
                >
                  <span>{field}</span>
                  <button
                    onClick={(e) => handleRemoveField(field, e)}
                    style={{
                      marginLeft: '8px',
                      color: 'var(--color-darkslateblue)',
                      background: 'none',
                      border: 'none',
                      cursor: 'pointer',
                      fontSize: '16px'
                    }}
                  >
                    ×
                  </button>
                </div>
              ))}
            </div>
          )}
          
          {/* 필드 옵션들 */}
          {fieldOptions.map((field, index) => (
            <div 
              key={index}
              style={{
                width: '350px',
                height: '40px',
                cursor: 'pointer',
                borderBottom: '1px solid var(--color-lightsteelblue)',
                backgroundColor: selectedFields.includes(field) ? 'var(--color-lavender)' : 'white'
              }}
              onClick={() => handleFieldSelect(field)}
              onMouseEnter={(e) => {
                if (!selectedFields.includes(field)) {
                  e.target.style.backgroundColor = '#f8f9fa';
                }
              }}
              onMouseLeave={(e) => {
                if (!selectedFields.includes(field)) {
                  e.target.style.backgroundColor = 'white';
                }
              }}
            >
              <div style={{
                height: '100%',
                width: '93.71%',
                margin: '0 auto',
                borderRadius: '8px',
                display: 'flex',
                alignItems: 'center',
                justifyContent: 'space-between',
                padding: '0 16px'
              }}>
                <div style={{
                  color: 'var(--color-gray-200)',
                  lineHeight: '150%',
                  fontSize: '15px'
                }}>
                  {field}
                </div>
                {selectedFields.includes(field) && (
                  <div style={{
                    width: '16px',
                    height: '16px',
                    backgroundColor: 'var(--color-darkslateblue)',
                    borderRadius: '50%',
                    display: 'flex',
                    alignItems: 'center',
                    justifyContent: 'center',
                    color: 'white',
                    fontSize: '12px'
                  }}>
                    ✓
                  </div>
                )}
              </div>
            </div>
          ))}
        </div>
      )}

      {/* 배경 클릭시 닫기 */}
      {isOpen && (
        <div 
          style={{
            position: 'fixed',
            top: 0,
            left: 0,
            right: 0,
            bottom: 0,
            zIndex: 999
          }}
          onClick={() => setIsOpen(false)}
        />
      )}
    </div>
  );
};

const Companies = () => {
  // PostgreSQL 테이블 구조에 맞춘 폼 데이터 상태 관리
  const [formData, setFormData] = useState({
    representative_name: '',        // 대표자명
    representative_gender: '',      // 대표자 성별  
    name: '',                      // 상호(법인명)
    business_number: '',           // 사업자등록번호
    established_at: '',            // 설립일자
    employee_count: '',            // 직원 수
    company_type: '중소기업',       // 기업분류
    preferred_fields: '',          // 선호분야
    location: '',                  // 소재지
    business_form: ''              // 기업형태
  });

  const [loading, setLoading] = useState(false);
  const [errors, setErrors] = useState({});

  // 입력값 변경 핸들러
  const handleInputChange = (field, value) => {
    setFormData(prev => ({
      ...prev,
      [field]: value
    }));
    
    // 에러 상태 초기화
    if (errors[field]) {
      setErrors(prev => ({
        ...prev,
        [field]: ''
      }));
    }
  };

  // 폼 유효성 검사
  const validateForm = () => {
    const newErrors = {};
    
    if (!formData.representative_name.trim()) {
      newErrors.representative_name = '대표자명을 입력해주세요.';
    }
    
    if (!formData.representative_gender) {
      newErrors.representative_gender = '대표자 성별을 선택해주세요.';
    }
    
    if (!formData.business_number.trim()) {
      newErrors.business_number = '사업자등록번호를 입력해주세요.';
    } else if (!/^\d{3}-\d{2}-\d{5}$/.test(formData.business_number)) {
      newErrors.business_number = '올바른 사업자등록번호 형식을 입력해주세요. (예: 123-45-67890)';
    }
    
    if (!formData.name.trim()) {
      newErrors.name = '상호(법인명)을 입력해주세요.';
    }

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  // 데이터베이스 저장 함수
  const saveToDatabase = async (data) => {
    try {
      const response = await fetch('/api/companies', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(data)
      });

      if (!response.ok) {
        throw new Error('저장에 실패했습니다.');
      }

      const result = await response.json();
      return result;
    } catch (error) {
      console.error('Database save error:', error);
      throw error;
    }
  };

  // 저장 버튼 클릭 핸들러
  const handleSave = async () => {
    if (!validateForm()) {
      alert('필수 항목을 모두 입력해주세요.');
      return;
    }

    setLoading(true);
    try {
      await saveToDatabase(formData);
      alert('정보가 성공적으로 저장되었습니다.');
    } catch (error) {
      alert('저장 중 오류가 발생했습니다. 다시 시도해주세요.');
    } finally {
      setLoading(false);
    }
  };

  const onGroupContainerClick = useCallback(() => {
    // Add your code here
  }, []);

  return (
    <div className={styles.ver2}>
      <div className={styles.div}>
        <img className={styles.child} alt="" src="Vector 573.svg" />
        <div className={styles.item} />
        <img className={styles.inner} alt="" src="Group 1000002831.svg" />
        <b className={styles.b}>이어봄</b>
        <img className={styles.vectorIcon} alt="" src="Vector.svg" />
        <div className={styles.vectorParent}>
          <img className={styles.groupChild} alt="" src="Vector 606.svg" />
          <div className={styles.wrapper}>
            <b className={styles.b1}>기본 정보</b>
          </div>
          <div className={styles.groupParent}>
            <div className={styles.parent}>
              <div className={styles.div1}>대표자명</div>
              <div style={{
                position: 'absolute',
                left: '107px',
                top: '1px',
                width: '100%',
                maxWidth: '200px'
              }}>
                <input
                  type="text"
                  value={formData.representative_name}
                  onChange={(e) => handleInputChange('representative_name', e.target.value)}
                  placeholder="대표자명을 입력하세요"
                  style={{
                    width: '100%',
                    position: 'relative',
                    borderRadius: '5px',
                    backgroundColor: 'var(--color-white)',
                    border: errors.representative_name ? '1px solid red' : '1px solid var(--color-lightsteelblue)',
                    boxSizing: 'border-box',
                    height: '39px',
                    outline: 'none',
                    padding: '0 12px',
                    fontSize: '14px'
                  }}
                />
                {errors.representative_name && (
                  <div style={{color: 'red', fontSize: '12px', position: 'absolute', top: '41px', left: 0}}>
                    {errors.representative_name}
                  </div>
                )}
              </div>
              <div className={styles.div3}>대표자 성별</div>
              <div className={styles.text}>{` `}</div>
              <div className={styles.inputBasic} />
              <div className={styles.rectangleParent}>
                <div 
                  className={styles.groupItem}
                  onClick={() => handleInputChange('representative_gender', '남')}
                  style={{
                    cursor: 'pointer',
                    backgroundColor: formData.representative_gender === '남' ? '#007bff' : ''
                  }}
                />
                <div className={styles.div4}>남</div>
                <div 
                  className={styles.groupInner}
                  onClick={() => handleInputChange('representative_gender', '여')}
                  style={{
                    cursor: 'pointer',
                    backgroundColor: formData.representative_gender === '여' ? '#007bff' : ''
                  }}
                />
                <b className={styles.b2}>여</b>
              </div>
              {errors.representative_gender && (
                <div style={{color: 'red', fontSize: '12px', marginTop: '5px'}}>
                  {errors.representative_gender}
                </div>
              )}
            </div>
            <div className={styles.inputBasicParent}>
              <div className={styles.inputBasic1}>
                <div className={styles.inputBasic2}>
                  <div className={styles.bg} />
                  <input
                    type="text"
                    value={formData.business_number}
                    onChange={(e) => handleInputChange('business_number', e.target.value)}
                    placeholder="123-45-67890"
                    className={styles.div5}
                    style={{
                      border: 'none',
                      outline: 'none',
                      background: 'transparent',
                      width: '100%',
                      position: 'absolute',
                      top: 0,
                      left: 0,
                      padding: '10px 12px',
                      fontSize: '14px'
                    }}
                  />
                </div>
              </div>
              <div className={styles.div6}>상호(법인명)</div>
              {/* 상호(법인명) 입력 필드 추가 */}
              <div style={{
                position: 'absolute',
                left: '107px',
                top: '66px',
                width: '200px',
                height: '39px'
              }}>
                <div style={{
                  width: '100%',
                  position: 'relative',
                  borderRadius: '5px',
                  backgroundColor: 'var(--color-white)',
                  border: errors.name ? '1px solid red' : '1px solid var(--color-lightsteelblue)',
                  boxSizing: 'border-box',
                  height: '39px'
                }}>
                  <input
                    type="text"
                    value={formData.name}
                    onChange={(e) => handleInputChange('name', e.target.value)}
                    placeholder="회사명을 입력하세요"
                    style={{
                      width: '100%',
                      height: '100%',
                      border: 'none',
                      outline: 'none',
                      background: 'transparent',
                      padding: '0 12px',
                      fontSize: '14px'
                    }}
                  />
                </div>
                {errors.name && (
                  <div style={{color: 'red', fontSize: '12px', position: 'absolute', top: '41px', left: 0}}>
                    {errors.name}
                  </div>
                )}
              </div>
              <div className={styles.div8}>사업자등록번호</div>
              <div className={styles.button}>
                <div className={styles.div9}>사업자 변경</div>
              </div>
              {errors.business_number && (
                <div style={{color: 'red', fontSize: '12px', marginTop: '5px', clear: 'both'}}>
                  {errors.business_number}
                </div>
              )}
              <div className={styles.text1}>{` `}</div>
              <div className={styles.inputBasic3}>
                <div className={styles.bg1} />
                <input
                  type="text"
                  value={formData.preferred_fields}
