        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(dataToSave),
      });
      
      if (!response.ok) throw new Error('저장 실패');
      
      alert('재무정보가 성공적으로 저장되었습니다.');
      navigate('/'); // 다음 단계로 이동
