import { useCallback, useState } from 'react';
import styles from './css/Companies.module.css';

const Companies = () => {
  // 폼 데이터 상태 관리 (PostgreSQL 테이블 구조에 맞춤)
  const [formData, setFormData] = useState({
    representative_name: '',        // 대표자명
    representative_gender: '',      // 대표자 성별  
    name: '',                      // 상호(법인명)
    business_number: '',           // 사업자등록번호
    established_at: '',            // 설립일자
    employee_count: '',            // 직원 수
    company_type: '중소기업',       // 기업분류
    preferred_fields: '',          // 선호분야 (text 타입)
    location: '',                  // 소재지 (전체 주소)
    business_form: ''              // 기업형태
  });

  const [loading, setLoading] = useState(false);
  const [errors, setErrors] = useState({});

  // 입력값 변경 핸들러
  const handleInputChange = (field, value) => {
    setFormData(prev => ({
      ...prev,
      [field]: value
    }));
    
    // 에러 상태 초기화
    if (errors[field]) {
      setErrors(prev => ({
        ...prev,
        [field]: ''
      }));
    }
  };

  // 업종 추가 핸들러 (preferred_fields를 콤마로 구분된 문자열로 관리)
  const handleAddPreferredField = (field) => {
    const currentFields = formData.preferred_fields ? formData.preferred_fields.split(',').map(f => f.trim()) : [];
    if (!currentFields.includes(field)) {
      const newFields = [...currentFields, field].join(', ');
      setFormData(prev => ({
        ...prev,
        preferred_fields: newFields
      }));
    }
  };

  // 업종 제거 핸들러
  const handleRemovePreferredField = (fieldToRemove) => {
    const currentFields = formData.preferred_fields ? formData.preferred_fields.split(',').map(f => f.trim()) : [];
    const newFields = currentFields.filter(field => field !== fieldToRemove).join(', ');
    setFormData(prev => ({
      ...prev,
      preferred_fields: newFields
    }));
  };

  // 폼 유효성 검사 (PostgreSQL 컬럼명에 맞춤)
  const validateForm = () => {
    const newErrors = {};
    
    if (!formData.representative_name.trim()) {
      newErrors.representative_name = '대표자명을 입력해주세요.';
    }
    
    if (!formData.representative_gender) {
      newErrors.representative_gender = '대표자 성별을 선택해주세요.';
    }
    
    if (!formData.business_number.trim()) {
      newErrors.business_number = '사업자등록번호를 입력해주세요.';
    } else if (!/^\d{3}-\d{2}-\d{5}$/.test(formData.business_number)) {
      newErrors.business_number = '올바른 사업자등록번호 형식을 입력해주세요. (예: 123-45-67890)';
    }
    
    if (!formData.name.trim()) {
      newErrors.name = '상호(법인명)을 입력해주세요.';
    }
    
    if (!formData.established_at.trim()) {
      newErrors.established_at = '설립일자를 입력해주세요.';
    }
    
    if (!formData.employee_count.trim()) {
      newErrors.employee_count = '직원 수를 입력해주세요.';
    }

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  // 데이터베이스 저장 함수
  const saveToDatabase = async (data) => {
    try {
      const response = await fetch('/api/companies', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(data)
      });

      if (!response.ok) {
        throw new Error('저장에 실패했습니다.');
      }

      const result = await response.json();
      return result;
    } catch (error) {
      console.error('Database save error:', error);
      throw error;
    }
  };

  // 저장 버튼 클릭 핸들러
  const handleSave = async () => {
    if (!validateForm()) {
      alert('필수 항목을 모두 입력해주세요.');
      return;
    }

    setLoading(true);
    try {
      await saveToDatabase(formData);
      alert('정보가 성공적으로 저장되었습니다.');
      // 다음 단계로 이동하는 로직 추가 가능
    } catch (error) {
      alert('저장 중 오류가 발생했습니다. 다시 시도해주세요.');
    } finally {
      setLoading(false);
    }
  };

  // 탭 전환 핸들러
  const onGroupContainerClick = useCallback(() => {
    // 탭 전환 로직 추가
  }, []);

  return (
    <div className={styles.ver2}>
      <div className={styles.div}>
        <img className={styles.child} alt="" src="Vector 573.svg" />
        <div className={styles.item} />
        <img className={styles.inner} alt="" src="Group 1000002831.svg" />
        <b className={styles.b}>이어봄</b>
        <img className={styles.vectorIcon} alt="" src="Vector.svg" />
        
        <div className={styles.vectorParent}>
          <img className={styles.groupChild} alt="" src="Vector 606.svg" />
          <div className={styles.wrapper}>
            <b className={styles.b1}>기본 정보</b>
          </div>
          
          <div className={styles.groupParent}>
            {/* 대표자명 및 성별 */}
            <div className={styles.parent}>
              <div className={styles.div1}>대표자명</div>
              <input
                type="text"
                value={formData.representative_name}
                onChange={(e) => handleInputChange('representative_name', e.target.value)}
                placeholder="대표자명을 입력하세요"
                className={`${styles.div2} ${errors.representative_name ? styles.error : ''}`}
                style={{
                  border: '1px solid #ddd',
                  padding: '8px',
                  borderRadius: '4px',
                  width: '150px'
                }}
              />
              {errors.representative_name && (
                <div className={styles.errorMessage} style={{color: 'red', fontSize: '12px'}}>
                  {errors.representative_name}
                </div>
              )}
              
              <div className={styles.div3}>대표자 성별</div>
              <div className={styles.rectangleParent}>
                <div 
                  className={`${styles.groupItem} ${formData.representative_gender === '남' ? styles.selected : ''}`}
                  onClick={() => handleInputChange('representative_gender', '남')}
                  style={{
                    cursor: 'pointer',
                    backgroundColor: formData.representative_gender === '남' ? '#007bff' : '#f8f9fa'
                  }}
                />
                <div className={styles.div4}>남</div>
                <div 
                  className={`${styles.groupInner} ${formData.representative_gender === '여' ? styles.selected : ''}`}
                  onClick={() => handleInputChange('representative_gender', '여')}
                  style={{
                    cursor: 'pointer',
                    backgroundColor: formData.representative_gender === '여' ? '#007bff' : '#f8f9fa'
                  }}
                />
                <b className={styles.b2}>여</b>
              </div>
              {errors.representative_gender && (
                <div className={styles.errorMessage} style={{color: 'red', fontSize: '12px'}}>
                  {errors.representative_gender}
                </div>
              )}
            </div>

            {/* 상호 및 사업자등록번호 */}
            <div className={styles.inputBasicParent}>
              <div className={styles.inputBasic1}>
                <div className={styles.inputBasic2}>
                  <div className={styles.bg} />
                  <input
                    type="text"
                    value={formData.business_number}
                    onChange={(e) => handleInputChange('business_number', e.target.value)}
                    placeholder="123-45-67890"
                    className={styles.div5}
                    style={{
                      border: 'none',
                      outline: 'none',
                      background: 'transparent',
                      width: '100%'
                    }}
                  />
                </div>
              </div>
              <div className={styles.div6}>상호(법인명)</div>
              <input
                type="text"
                value={formData.name}
                onChange={(e) => handleInputChange('name', e.target.value)}
                placeholder="회사명을 입력하세요"
                className={styles.div7}
                style={{
                  border: '1px solid #ddd',
                  padding: '8px',
                  borderRadius: '4px'
                }}
              />
              <div className={styles.div8}>사업자등록번호</div>
              {errors.business_number && (
                <div style={{color: 'red', fontSize: '12px'}}>
                  {errors.business_number}
                </div>
              )}
              
              <div className={styles.inputBasic3}>
                <div className={styles.bg1} />
                <select
                  value={formData.businessCategory}
                  onChange={(e) => handleInputChange('businessCategory', e.target.value)}
                  className={styles.div10}
                  style={{
                    border: 'none',
                    outline: 'none',
                    background: 'transparent',
                    width: '100%'
                  }}
                >
                  <option value="">대분류/중분류 선택</option>
                  <option value="manufacturing">제조업</option>
                  <option value="service">서비스업</option>
                  <option value="it">정보통신업</option>
                  <option value="construction">건설업</option>
                </select>
              </div>
            </div>

            {/* 설립일자 및 직원 수 */}
            <div className={styles.group}>
              <div className={styles.div12}>설립일자</div>
              <div className={styles.div13}>직원 수</div>
              <div className={styles.inputBasicGroup}>
                <div className={styles.inputBasic4}>
                  <div className={styles.inputBasic2}>
                    <div className={styles.bg2} />
                    <input
                      type="number"
                      value={formData.employee_count}
                      onChange={(e) => handleInputChange('employee_count', e.target.value)}
                      placeholder="0"
                      style={{
                        border: 'none',
                        outline: 'none',
                        background: 'transparent',
                        width: '80%'
                      }}
                    />
                  </div>
                </div>
                <div className={styles.div14}>명</div>
              </div>
              <div className={styles.inputBasic6}>
                <div className={styles.bg} />
                <input
                  type="date"
                  value={formData.established_at}
                  onChange={(e) => handleInputChange('established_at', e.target.value)}
                  className={styles.div15}
                  style={{
                    border: 'none',
                    outline: 'none',
                    background: 'transparent',
                    width: '100%'
                  }}
                />
              </div>
            </div>

            {/* 기업분류 및 선호분야 */}
            <div className={styles.container}>
              <div className={styles.div12}>기업분류</div>
              <div className={styles.div17}>선호분야</div>
              <div className={styles.inputBasic7}>
                <div className={styles.bg4} />
                <select
                  value={formData.company_type}
                  onChange={(e) => handleInputChange('company_type', e.target.value)}
                  className={styles.b3}
                  style={{
                    border: 'none',
                    outline: 'none',
                    background: 'transparent',
                    fontWeight: 'bold'
                  }}
                >
                  <option value="중소기업">중소기업</option>
                  <option value="대기업">대기업</option>
                  <option value="스타트업">스타트업</option>
                </select>
              </div>
              <textarea
                value={formData.preferred_fields}
                onChange={(e) => handleInputChange('preferred_fields', e.target.value)}
                placeholder="선호분야를 입력하세요 (콤마로 구분)"
                style={{
                  border: '1px solid #ddd',
                  padding: '8px',
                  borderRadius: '4px',
                  minHeight: '60px',
                  resize: 'vertical'
                }}
              />
            </div>

            {/* 소재지 정보 - location 필드로 통합 */}
            <div className={styles.groupContainer}>
              <div className={styles.groupDiv}>
                <div className={styles.frame}>
                  <div className={styles.div19}>소재지</div>
                </div>
              </div>
              <div className={styles.inputBasic10}>
                <div className={styles.bg} />
                <textarea
                  value={formData.location}
                  onChange={(e) => handleInputChange('location', e.target.value)}
                  placeholder="전체 주소를 입력하세요"
                  className={styles.div5}
                  style={{
                    border: 'none',
                    outline: 'none',
                    background: 'transparent',
                    width: '100%',
                    minHeight: '60px',
                    resize: 'vertical'
                  }}
                />
              </div>
            </div>

            {/* 기업형태 */}
            <div className={styles.parent1}>
              <div className={styles.div23}>기업형태</div>
              <div className={styles.groupWrapper}>
                <div className={styles.inputBasic4}>
                  <div className={styles.inputBasic8}>
                    <div className={styles.inputBasic2}>
                      <div className={styles.bg} />
                      <input
                        type="text"
                        value={formData.business_form}
                        onChange={(e) => handleInputChange('business_form', e.target.value)}
                        placeholder="기업형태를 입력하세요"
                        className={styles.div18}
                        style={{
                          border: 'none',
                          outline: 'none',
                          background: 'transparent'
                        }}
                      />
                    </div>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>

        {/* 탭 섹션들 */}
        <div className={styles.parent2}>
          <b className={styles.b4}>기업개요</b>
          <div className={styles.rectangleDiv} />
        </div>
        <div className={styles.parent3} onClick={onGroupContainerClick}>
          <b className={styles.b4}>재무정보</b>
          <div className={styles.groupChild1} />
        </div>
        <div className={styles.parent4} onClick={onGroupContainerClick}>
          <b className={styles.b4}>사업정보</b>
          <div className={styles.groupChild1} />
        </div>
        <div className={styles.parent5} onClick={onGroupContainerClick}>
          <b className={styles.b4}>가점정보</b>
          <div className={styles.groupChild1} />
        </div>

        {/* 관리자 선택 제거 (PostgreSQL 테이블에 없음) */}
      </div>

      {/* 나머지 UI 컴포넌트들은 원본 유지 */}
      <div className={styles.ver2Child} />
      <div className={styles.frameDiv}>
        {/* 프로필 완성도 및 기타 UI 컴포넌트들 */}
        <div className={styles.rectangleGroup}>
          <div className={styles.groupChild4} />
          <img className={styles.locked1f5122Icon} alt="" src="locked_1f512 2.png" />
          <div className={styles.div33}>입력 정보는 안전하게 보호됩니다</div>
        </div>
        {/* ... 기타 UI 컴포넌트들 ... */}
      </div>

      {/* 헤더 및 사이드바 */}
      <div className={styles.header}>
        {/* 헤더 내용 */}
      </div>

      {/* 푸터 - 저장 버튼 */}
      <div className={styles.footer}>
        <div className={styles.component13Parent}>
          <div className={styles.component13} onClick={handleSave}>
            <div className={styles.component13Child} />
            <b className={styles.b13}>
              {loading ? '저장 중...' : '저장 및 다음단계'}
            </b>
            <img className={styles.caretrightIcon} alt="" src="CaretRight.svg" />
          </div>
          <div className={styles.component26}>
            <div className={styles.component26Child} />
            <b className={styles.b14}>건너뛰기</b>
          </div>
        </div>
      </div>

      {/* 사이드바 */}
      <div className={styles.rectangleParent4}>
        {/* 사이드바 내용 */}
      </div>
    </div>
  );
};

export default Companies;
