import { useEffect, useState, useCallback } from 'react';
import styles from './css/Financial.module.css';
import { useParams, useNavigate } from 'react-router-dom';
import header from '../asset/header.svg';
import widget from '../asset/widget2.svg';
import lnb from '../asset/LNB2.svg';
import icon from '../asset/icon.svg';
import upload from '../asset/upload.svg';
import Vector from '../asset/Vector.svg';

const Financial = () => {
  const { id } = useParams();
  const navigate = useNavigate();

  const [financialTable, setFinancialTable] = useState({
    매출액: { 2021: '', 2022: '', 2023: '', 2024: '', 2025: '' },
    총부채: { 2021: '', 2022: '', 2023: '', 2024: '', 2025: '' },
    총자본: { 2021: '', 2022: '', 2023: '', 2024: '', 2025: '' },
    총자산: { 2021: '', 2022: '', 2023: '', 2024: '', 2025: '' },
    자본금: { 2021: '', 2022: '', 2023: '', 2024: '', 2025: '' }
  });

  const [analysisData, setAnalysisData] = useState({
    자본잠식률: '',
    부채비율: '',
    자본잠식상태: '해당없음'
  });

  const [performanceData, setPerformanceData] = useState({
    자부담가능액: '',
    신용평가등급: '선택'
  });

  useEffect(() => {
    const fetchFinancialData = async () => {
      try {
        const response = await fetch(`http://localhost:5000/api/financials/${id}`);
        if (!response.ok) throw new Error('불러오기 실패');
        const data = await response.json();
        if (data.financialTable) setFinancialTable(data.financialTable);
        if (data.analysisData) setAnalysisData(data.analysisData);
        if (data.performanceData) setPerformanceData(data.performanceData);
      } catch (err) {
        console.error('재무정보 불러오기 실패:', err.message);
      }
    };
    if (id) fetchFinancialData();
  }, [id]);

  const handleTableInputChange = (category, year, value) => {
    const numericValue = value.replace(/[^0-9]/g, '');
    setFinancialTable(prev => ({
      ...prev,
      [category]: {
        ...prev[category],
        [year]: numericValue
      }
    }));
    calculateFinancialRatios();
  };

  const calculateFinancialRatios = () => {
    const { 총자본, 총부채, 자본금 } = financialTable;
    const capital = parseFloat(자본금['2025']) || 0;
    const equity = parseFloat(총자본['2025']) || 0;
    const debt = parseFloat(총부채['2025']) || 0;

    const 부채비율 = equity > 0 ? ((debt / equity) * 100).toFixed(2) : '0.00';
    const 자본잠식률 = capital > 0 ? (((capital - equity) / capital) * 100).toFixed(2) : '0.00';
    const 자본잠식상태 = 자본잠식률 > 0 ? '자본잠식' : '해당없음';

    setAnalysisData({ 부채비율, 자본잠식률, 자본잠식상태 });
  };

  const saveFinancialData = async () => {
    try {
      const dataToSave = { financialTable, analysisData, performanceData };
      const response = await fetch(`http://localhost:5000/api/financials/${id}`, {
        method: 'PUT',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(dataToSave)
      });
      if (!response.ok) throw new Error('저장 실패');
      alert('재무정보가 성공적으로 저장되었습니다.');
      navigate('/');
    } catch (err) {
      alert('저장에 실패했습니다. 다시 시도해주세요.');
    }
  };

  const years = ['2021', '2022', '2023', '2024', '2025'];
  const categories = ['매출액', '총부채', '총자본', '총자산', '자본금'];

  return (
    <div className={styles.ver2}>
      <img src={header} alt="header" />
      <table className={styles.table}>
        <thead>
          <tr>
            <th>구분</th>
            {years.map(year => <th key={year}>{year}</th>)}
          </tr>
        </thead>
        <tbody>
          {categories.map((category, idx) => (
            <tr key={category}>
              <td>{category}</td>
              {years.map(year => (
                <td key={year}>
                  <input
                    type="text"
                    value={financialTable[category][year]}
                    onChange={(e) => handleTableInputChange(category, year, e.target.value)}
                    style={{ width: '180px', height: idx < 2 ? '30px' : '40px', textAlign: 'right', padding: '0 8px' }}
                  />
                </td>
              ))}
            </tr>
          ))}
        </tbody>
      </table>

      <div>
        <h4>재무건전성 분석</h4>
        <p>자본잠식률: {analysisData.자본잠식률}%</p>
        <p>부채비율: {analysisData.부채비율}%</p>
        <p>자본잠식상태: {analysisData.자본잠식상태}</p>
      </div>

      <div>
        <h4>자부담 가능액</h4>
        <input
          type="text"
          value={performanceData.자부담가능액}
          onChange={(e) => handlePerformanceInputChange('자부담가능액', e.target.value)}
        /> 천 원

        <h4>신용평가등급</h4>
        <select
          value={performanceData.신용평가등급}
          onChange={(e) => handlePerformanceInputChange('신용평가등급', e.target.value)}>
          <option value="선택">선택</option>
          <option value="AAA">AAA</option>
          <option value="AA">AA</option>
          <option value="A">A</option>
          <option value="BBB">BBB</option>
          <option value="BB">BB</option>
          <option value="B">B</option>
          <option value="CCC">CCC</option>
          <option value="CC">CC</option>
          <option value="C">C</option>
          <option value="D">D</option>
        </select>
      </div>

      <button onClick={saveFinancialData}>저장 및 다음단계</button>
      <img src={lnb} alt="lnb" />
      <img src={widget} alt="widget" />
    </div>
  );
};

export default Financial;
